    public void refreshAccounts(String customerId) {
        String url = config.getApiUrl() + "/aggregation/v1/customers/" + customerId + "/accounts";

        HttpHeaders headers = new HttpHeaders();
        headers.add("Content-Type", "application/json");
        headers.add("Finicity-App-Key", config.getAppKey());
        headers.add("Finicity-App-Token", getAccessToken());

        HttpEntity<?> request = new HttpEntity<>(headers);

        restTemplate.exchange(
            url,
            HttpMethod.POST,
            request,
            JsonNode.class
        );
    }

    public JsonNode getCustomerAccounts(String customerId) {
        String url = config.getApiUrl() + "/aggregation/v1/customers/" + customerId + "/accounts";

        HttpHeaders headers = new HttpHeaders();
        headers.add("Finicity-App-Key", config.getAppKey());
        headers.add("Finicity-App-Token", getAccessToken());

        HttpEntity<?> request = new HttpEntity<>(headers);

        ResponseEntity<JsonNode> response = restTemplate.exchange(
            url,
            HttpMethod.GET,
            request,
            JsonNode.class
        );

        return response.getBody();
    }
}

    @GetMapping("/refresh/{customerId}")
    public String refreshAccounts(@PathVariable String customerId) {
        openBankingService.refreshAccounts(customerId);
        return "redirect:/accounts/" + customerId;
    }

    @GetMapping("/accounts/{customerId}")
    public String getAccounts(@PathVariable String customerId, Model model) {
        Customer customer = customerRepository.findByMcpCustomerId(customerId);
        if (customer == null) {
            return "redirect:/";
        }

        var accountsData = openBankingService.getCustomerAccounts(customerId);
        model.addAttribute("accounts", accountsData.get("accounts"));
        model.addAttribute("customerId", customerId);

        return "accounts";
    }
}

