{"filePath": "/Users/sudheerghanathe/Code/mcpdemo/src/main/java/com/example/mcpdemo/service/CurrencyConversionService.java", "content": "package com.example.mcpdemo.service;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;
import org.springframework.http.*;
import com.mastercard.developer.oauth.OAuth;
import com.fasterxml.jackson.annotation.JsonProperty;
import java.security.PrivateKey;
import java.io.FileInputStream;
import java.security.KeyStore;
import java.net.URI;
import lombok.Data;

@Service
public class CurrencyConversionService {

    @Value("${mastercard.currency.api.url}")
    private String apiUrl;

    @Value("${mastercard.currency.api.consumer.key}")
    private String consumerKey;

    @Value("${mastercard.currency.api.keystore.path}")
    private String keystorePath;

    @Value("${mastercard.currency.api.keystore.password}")
    private String keystorePassword;

    @Value("${mastercard.currency.api.keystore.alias}")
    private String keystoreAlias;

    private final RestTemplate restTemplate;

    public CurrencyConversionService(RestTemplate restTemplate) {
        this.restTemplate = restTemplate;
    }

    public double convertToINR(double amount, String fromCurrency) {
        return convertAmount(amount, fromCurrency, "INR");
    }

    public double convertAmount(double amount, String fromCurrency, String toCurrency) {
        try {
            // Load the signing key
            PrivateKey signingKey = loadSigningKey();

            // Create the request URL for the correct Currency Conversion Calculator API endpoint
            String requestUrl = String.format("%s/currencyconversion/transactionrates/conversion-details?fxDate=0000-00-00&transCurr=%s&transAmt=%.2f&crdhldBillCurr=%s",
                    apiUrl, fromCurrency, amount, toCurrency);

            // Sign the request
            String authHeader = OAuth.getAuthorizationHeader(
                new URI(requestUrl),
                "GET",
                "",
                java.nio.charset.StandardCharsets.UTF_8,
                consumerKey,
                signingKey
            );

            // Add the authorization header
            HttpHeaders headers = new HttpHeaders();
            headers.add("Authorization", authHeader);

            // Make the API call
            HttpEntity<?> entity = new HttpEntity<>(headers);
            ResponseEntity<ConversionDetailResponse> response = restTemplate.exchange(
                requestUrl,
                HttpMethod.GET,
                entity,
                ConversionDetailResponse.class
            );

            return response != null && response.getBody() != null ? 
                   response.getBody().getData().getConversionRate() * amount : amount;
        } catch (Exception e) {
            // Log the error and return original amount if conversion fails
            e.printStackTrace();
            return amount;
        }
    }

    private PrivateKey loadSigningKey() throws Exception {
        KeyStore keystore = KeyStore.getInstance("PKCS12");
        FileInputStream is = new FileInputStream(keystorePath);
        keystore.load(is, keystorePassword.toCharArray());
        return (PrivateKey) keystore.getKey(keystoreAlias, keystorePassword.toCharArray());
    }

    @Data
    static class ConversionDetailResponse {
        private ConversionData data;
    }

    @Data
    static class ConversionData {
        @JsonProperty("conversionRate")
        private double conversionRate;

        @JsonProperty("crdhldBillCurr")
        private String cardholderBillingCurrency;

        @JsonProperty("transCurr")
        private String transactionCurrency;

        @JsonProperty("transAmt")
        private double transactionAmount;

        @JsonProperty("crdhldBillAmt")
        private double cardholderBillingAmount;
    }
}"}

